                        <div className="shade-name">{l.name}</div>
                        <div className="shade-meta">
                          <span className="finish-tag">{l.finish}</span>
                          <span className="vibe-tag">{l.vibe}</span>
                        </div>
                        <div className="shade-hex">{l.hex}</div>
                      </div>
